import React, { useState, useEffect } from "react";
import {
  View,
  Picker,
  StyleSheet,
  Text,
  SafeAreaView,
  ScrollView,
  AsyncStorage,
  Image,
  Modal,
  TouchableHighlight,
} from "react-native";
import { Icon } from "native-base";
import { Button, Title, Card } from "react-native-paper";
import { TouchableOpacity } from "react-native-gesture-handler";
import moment from "moment";
import axios from "axios";
import HeaderComp from "../Header";

export default function studentlist({ navigation }) {
  const [students, setStudents] = useState([]);
  const [u_name, setUname] = useState("");
  const [email, setEmail] = useState("");
  const [phone_no, setPhone_no] = useState("");
  const [password, setPassword] = useState("");
  const [picture, setPicture] = useState("");
  const [id, setId] = useState("");
  const [gender, setGender] = useState("male");
  const [dob, setDob] = useState("");
  const [roll_no, setRoll_no] = useState("");
  const [batch, setBatch] = useState("");
  const [class_name, setClassname] = useState("Class");
  const [section, setSection] = useState("");
  const [registration, setRegistration] = useState("");
  const [father_name, setFather_name] = useState("");
  let [classes, setClasses] = useState([]);

  useEffect(() => {
    getStudents();
  }, []);

  const showModal = (
    uname,
    e_mail,
    phone,
    image,
    gen,
    pass,
    fname,
    cname,
    rn,
    Id,
    d_o_b,
    bat,
    sec,
    reg
  ) => {
    setModalVisible(true);

    setUname(uname);
    setEmail(e_mail);
    setPhone_no(phone);
    setPicture(image);
    setPassword(pass);
    setGender(gen);
    setFather_name(fname);
    setClassname(cname);
    setRoll_no(rn);
    setId(Id);
    setDob(d_o_b);
    setBatch(bat);
    setSection(sec);
    setRegistration(reg);
  };

  const getStudents = () => {
    AsyncStorage.getItem("class").then((cdata) => {
      console.log(cdata);
      axios
        .post("http://krishma.webcodice.com/react-native/axios.php", {
          request: 30,
          class: cdata,
        })
        .then((response) => {
          //console.log(response.data);

          if (response.data == "no") {
            alert("No students");
          } else {
            setStudents(response.data);
          }
          //AsyncStorage.removeItem("class");
        })
        .catch(function (error) {
          console.log(error);
        });
    });
  };
  const [modalVisible, setModalVisible] = useState(false);
  const [name, setName] = useState("");

  AsyncStorage.getItem("user").then((data) => {
    // let user = data;
    setName(data);
  });

  return (
    <View style={styles.container}>
      <HeaderComp navigation={navigation} />
      <SafeAreaView style={styles.content}>
        <ScrollView style={styles.scroll}>
          <Text style={styles.welcome}>
            <Text>Welcome</Text>
            <Text> {name} </Text>
          </Text>

          <View style={styles.content}>
            <Title style={styles.welcome}>List of Students</Title>
            {students.map((x) => (
              <Card style={styles.mycard} key={x.id}>
                <View style={styles.cardView}>
                  <TouchableOpacity
                    onPress={() =>
                      showModal(
                        x.name,
                        x.email,
                        x.phone_no,
                        x.image,
                        x.gender,
                        x.username,
                        x.father_name,
                        x.class_name,
                        x.roll_no,
                        x.id,
                        x.dob,
                        x.batch,
                        x.section,
                        x.registration
                      )
                    }
                    key={x.id}
                  >
                    <Image
                      style={{ width: 60, height: 60, borderRadius: 30 }}
                      source={
                        x.image
                          ? { uri: x.image }
                          : {
                              uri:
                                "https://n8d.at/wp-content/plugins/aioseop-pro-2.4.11.1/images/default-user-image.png",
                            }
                      }
                    />
                  </TouchableOpacity>
                  <View style={{ marginLeft: 10 }}>
                    <Text style={styles.text}>{x.username}</Text>
                    <Text style={styles.textstyle}>{x.email}</Text>
