import React, { useState, useEffect } from "react";
import {
  View,
  Picker,
  StyleSheet,
  Text,
  AsyncStorage,
  Image,
  SafeAreaView,
  ScrollView,
} from "react-native";
import { Title, Card } from "react-native-paper";
import axios from "axios";
import HeaderComp from "../Header";
import QRCode from "react-qr-code";
export default function View_QR({ navigation }) {
  let [students, setStudents] = useState([]);

  useEffect(() => {
    getQRs();
  }, []);

  const getQRs = () => {
    axios
      .post("http://krishma.webcodice.com/react-native/axios.php", {
        request: 21,
      })
      .then((response) => {
        // console.log(response.data);
        setStudents(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  const [name, setName] = useState("");

  AsyncStorage.getItem("user").then((data) => {
    // let user = data;
    setName(data);
  });

  return (
    <View style={styles.container}>
      <HeaderComp navigation={navigation} />
      <SafeAreaView style={styles.content}>
        <ScrollView style={styles.scroll}>
          <Text style={styles.welcome}>
            <Text>Welcome</Text>
            <Text> {name} </Text>
          </Text>

          <View style={styles.content}>
            <Title style={styles.welcome}>List of QR's</Title>

            {students.map((x) => (
              <Card style={styles.mycard} key={x.id}>
                <View style={styles.cardView}>
                  <QRCode
                    height="100"
                    width="100"
                    value={
                      x.teacher +
                      x.class_name +
                      x.section +
                      x.subject +
                      x.date_time
                    }
                    size={50}
                  />

                  <View style={{ marginLeft: 10 }}>
                    <Text style={styles.text}>{x.teacher}</Text>
                    <Text style={styles.textstyle}>
                      {x.class_name + " " + x.section}
                    </Text>
                    <Text style={styles.textstyle}>{x.subject}</Text>
                  </View>
                </View>
              </Card>
            ))}
          </View>
        </ScrollView>
      </SafeAreaView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  scroll: {
    width: "100%",
  },
  content: {
    flex: 1,
    justifyContent: "center",
    marginHorizontal: 10,
  },
  button: {
    width: "60%",
    paddingTop: 8,
    marginTop: 10,
    paddingBottom: 8,
    backgroundColor: "#006aff",
    marginBottom: 20,
  },
  mycard: {
    margin: 5,
  },
  cardView: {
    flexDirection: "row",
    padding: 6,
  },
  text: {
    fontSize: 18,
  },
  textstyle: {
    fontSize: 14,
  },
  welcome: {
    fontSize: 20,
    textAlign: "center",
    margin: 10,
    fontWeight: "bold",
  },
});
