    setLoading(true);
    let reg = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;

    if (
      name == "" ||
      classname == "Class" ||
      classname == "" ||
      subject == "" ||
      subject == "Subject" ||
      phone == "" ||
      picture == "" ||
      email == "" ||
      password == ""
    ) {
      alert("All Field are required to field");
      setLoading(false);
    } else if (reg.test(email) === false) {
      alert("Invalid email");
      return false;
    } else {
      // alert("Your Data Saved:  "+gender+"Image :" +picture);
      axios
        .post("http://krishma.webcodice.com/react-native/axios.php", {
          request: 5,
          name: name,
          email: email,
          phone_no: phone,
          password: password,
          image: picture,
          username: username,
          gender: gender,
          dob: dob,
          class_name: classname,
          subject: subject,
          qualification: qualification,
