          onChangeText={(text) => setName(text)}
          theme={mytheme}
        />
        <TextInput
          style={styles.inputstyle}
          label="Email"
          value={email}
          keyboardType="email-address"
          mode="outlined"
          onChangeText={(text) => setEmail(text)}
          theme={mytheme}
        />

        <TextInput
          style={styles.inputstyle}
          label="Phone"
          value={phone}
          keyboardType="number-pad"
          mode="outlined"
          onChangeText={(text) => setPhone(text)}
          theme={mytheme}
        />

        <TextInput
          style={styles.inputstyle}
          label="Password"
          value={password}
          mode="outlined"
          secureTextEntry={true}
          onChangeText={(text) => setPassword(text)}
          theme={mytheme}
        />
        <TextInput
          style={styles.inputstyle}
          label="Username"
          value={username}
          mode="outlined"
          secureTextEntry={false}
          onChangeText={(text) => setUsername(text)}
          theme={mytheme}
        />

        <View style={{ flexDirection: "row" }}>
          <Text style={{ fontSize: 22 }}>Gender :</Text>
          <RadioButton
            style={styles.inputstyle}
            label="Gender"
            value={gender}
            mode="outlined"
            value="male"
            theme={mytheme}
            status={gender === "male" ? "checked" : "unchecked"}
            onPress={() => setGender("male")}
          />
          <Text style={{ fontSize: 20, padding: 6 }}>Male</Text>
          <RadioButton
            style={styles.inputstyle}
            theme={mytheme}
            value="female"
            status={gender === "female" ? "checked" : "unchecked"}
            onPress={() => setGender("female")}
          />
          <Text style={{ fontSize: 20, padding: 6 }}>Female</Text>
        </View>
        <DatePicker
          theme={mytheme}
          locale={"en"}
          defaultDate={new Date()}
          timeZoneOffsetInMinutes={undefined}
          modalTransparent={false}
          animationType={"fade"}
          androidMode={"default"}
          placeHolderText="Date of Birth"
          textStyle={styles.textstyle}
          placeHolderTextStyle={styles.datestyle}
          onDateChange={(dob) => setDob(dob)}
          disabled={false}
        />

        <Picker
          style={styles.pickerstyle}
          theme={mytheme}
          selectedValue={classname}
          onValueChange={(itemValue) => setClassname(itemValue)}
        >
          <Picker.Item label="Select Class" value="Class" />

          {classes.map((x) => (
            <Picker.Item
              key={x.class_id}
              label={x.class_name}
              value={x.class_name}
            />
          ))}
        </Picker>
        <Picker
          style={styles.pickerstyle}
          theme={mytheme}
          selectedValue={subject}
          onValueChange={(itemValue) => setSubject(itemValue)}
        >
          <Picker.Item label="Select Subject" value="Subject" />

          {subjects.map((x) => (
            <Picker.Item
              key={x.subject_id}
              label={x.subject_name}
              value={x.subject_name}
            />
          ))}
        </Picker>

        <TextInput
          style={styles.inputstyle}
          label="Qualification"
          value={qualification}
          mode="outlined"
          onChangeText={(text) => setQualification(text)}
          theme={mytheme}
        />
        <Button
          style={styles.inputstyle}
          icon={picture == "" ? "upload" : "check"}
          theme={mytheme}
          mode="contained"
          onPress={() => setModal(true)}
        >
          {picture == "" ? "upload Image" : "Image Uploaded"}
        </Button>

        <Button
          mode="contained"
          style={styles.inputstyle}
          theme={mytheme}
          onPress={() => validate()}
        >
          Register
        </Button>

        <Modal
          animationType="slide"
          transparent={true}
          visible={modal}
          onRequestClose={() => {
            setModal(false);
          }}
        >
          <View style={styles.modalView}>
            <View style={styles.modalstyle}>
              <Button
                style={styles.inputstyle}
                icon="content-save"
                mode="contained"
